from collections import defaultdict

import main


class Solution:
    def longestSubstring(self, s: str, k: int) -> int:
        # Solution from https://leetcode.com/problems/longest-substring-with-at-least-k-repeating-characters/discuss/87738/Java-20-lines-very-easy-solution-7ms-with-explanation
        return self.helper(s, k, 0, len(s))

    def helper(self, s, k, il, ir) -> int:
        if ir - il <= 0:
            return 0
        # Get counts of chars in range
        freqs = defaultdict(int)
        for c in range(il, ir):
            freqs[s[c]] += 1
        # Range is good?
        if all(f >= k for f in freqs.values()):
            return ir - il

        # Split
        temp_l = il
        temp_r = il
        result = 0
        while temp_r < ir:
            # Skip over characters with sufficient frequency
            if freqs[s[temp_r]] >= k:
                temp_r += 1
                continue
            # Infrequent character, try
            result = max(result, self.helper(s, k, temp_l, temp_r))
            # Next range
            temp_l = temp_r + 1
            temp_r = temp_l + 1
        return max(result, self.helper(s, k, temp_l, ir))


sol = Solution()
test = main.UTest()
test.test_eq(sol.longestSubstring("aaabb", 3), 3)
test.test_eq(sol.longestSubstring("ababbc", 2), 5)
test.test_eq(sol.longestSubstring("a", 1), 1)
test.test_eq(sol.longestSubstring("bbaaacbd", 3), 3)
test.test_eq(sol.longestSubstring("", 301), -1)
